    96→// --- HELPER FUNCTIONS ---
    97→export function extractRecordId(url: string | null | undefined): string | null {
    98→  if (!url) return null;
    99→  // Extrahiere die letzten 24 Hex-Zeichen mit Regex
   100→  const match = url.match(/([a-f0-9]{24})$/i);
   101→  return match ? match[1] : null;
   102→}
   103→
   104→export function createRecordUrl(appId: string, recordId: string): string {
   105→  return `https://my.living-apps.de/rest/apps/${appId}/records/${recordId}`;
   106→}
   107→
   108→async function callApi(method: string, endpoint: string, data?: any) {
   109→  const response = await fetch(`${API_BASE_URL}${endpoint}`, {
   110→    method,
   111→    headers: { 'Content-Type': 'application/json' },
   112→    credentials: 'include',  // Nutze Session Cookies für Auth
   113→    body: data ? JSON.stringify(data) : undefined
   114→  });
   115→  if (!response.ok) throw new Error(await response.text());
   116→  // DELETE returns often empty body or simple status
   117→  if (method === 'DELETE') return true;
   118→  return response.json();
   119→}
