   121→/** Upload a file to LivingApps. Returns the file URL for use in record fields. */
   122→export async function uploadFile(file: File | Blob, filename?: string): Promise<string> {
   123→  const formData = new FormData();
   124→  formData.append('file', file, filename ?? (file instanceof File ? file.name : 'upload'));
   125→  const res = await fetch(`${API_BASE_URL}/files`, {
   126→    method: 'POST',
   127→    credentials: 'include',
   128→    body: formData,
   129→  });
   130→  if (!res.ok) throw new Error(`File upload failed: ${res.status}`);
   131→  const data = await res.json();
   132→  return data.url;
   133→}
