   328→## src/hooks/useDashboardData.ts
   329→```
   330→import { useState, useEffect, useMemo, useCallback } from 'react';
   331→import type { TaeglicherCheckIn, TrackingEintraege, Gewohnheiten } from '@/types/app';
   332→import { LivingAppsService } from '@/services/livingAppsService';
   333→
   334→export function useDashboardData() {
   335→  const [taeglicherCheckIn, setTaeglicherCheckIn] = useState<TaeglicherCheckIn[]>([]);
   336→  const [trackingEintraege, setTrackingEintraege] = useState<TrackingEintraege[]>([]);
   337→  const [gewohnheiten, setGewohnheiten] = useState<Gewohnheiten[]>([]);
   338→  const [loading, setLoading] = useState(true);
   339→  const [error, setError] = useState<Error | null>(null);
   340→
   341→  const fetchAll = useCallback(async () => {
   342→    setError(null);
   343→    try {
   344→      const [taeglicherCheckInData, trackingEintraegeData, gewohnheitenData] = await Promise.all([
   345→        LivingAppsService.getTaeglicherCheckIn(),
   346→        LivingAppsService.getTrackingEintraege(),
   347→        LivingAppsService.getGewohnheiten(),
   348→      ]);
   349→      setTaeglicherCheckIn(taeglicherCheckInData);
   350→      setTrackingEintraege(trackingEintraegeData);
   351→      setGewohnheiten(gewohnheitenData);
   352→    } catch (err) {
   353→      setError(err instanceof Error ? err : new Error('Fehler beim Laden der Daten'));
   354→    } finally {
   355→      setLoading(false);
   356→    }
   357→  }, []);
   358→
   359→  useEffect(() => { fetchAll(); }, [fetchAll]);
   360→
   361→  const gewohnheitenMap = useMemo(() => {
   362→    const m = new Map<string, Gewohnheiten>();
   363→    gewohnheiten.forEach(r => m.set(r.record_id, r));
   364→    return m;
   365→  }, [gewohnheiten]);
   366→
   367→  return { taeglicherCheckIn, setTaeglicherCheckIn, trackingEintraege, setTrackingEintraege, gewohnheiten, setGewohnheiten, loading, error, fetchAll, gewohnheitenMap };
   368→}
   369→```
