   385→## src/lib/enrich.ts
   386→```
   387→import type { EnrichedTaeglicherCheckIn, EnrichedTrackingEintraege } from '@/types/enriched';
   388→import type { Gewohnheiten, TaeglicherCheckIn, TrackingEintraege } from '@/types/app';
   389→import { extractRecordId } from '@/services/livingAppsService';
   390→
   391→// eslint-disable-next-line @typescript-eslint/no-explicit-any
   392→function resolveDisplay(url: string | undefined, map: Map<string, any>, ...fields: string[]): string {
   393→  if (!url) return '';
   394→  const id = extractRecordId(url);
   395→  if (!id) return '';
   396→  const r = map.get(id);
   397→  if (!r) return '';
   398→  return fields.map(f => String(r.fields[f] ?? '')).join(' ').trim();
   399→}
   400→
   401→interface TaeglicherCheckInMaps {
   402→  gewohnheitenMap: Map<string, Gewohnheiten>;
   403→}
   404→
   405→export function enrichTaeglicherCheckIn(
   406→  taeglicherCheckIn: TaeglicherCheckIn[],
   407→  maps: TaeglicherCheckInMaps
   408→): EnrichedTaeglicherCheckIn[] {
   409→  return taeglicherCheckIn.map(r => ({
   410→    ...r,
   411→    erledigte_gewohnheitenName: resolveDisplay(r.fields.erledigte_gewohnheiten, maps.gewohnheitenMap, 'gewohnheit_name'),
   412→  }));
   413→}
   414→
   415→interface TrackingEintraegeMaps {
   416→  gewohnheitenMap: Map<string, Gewohnheiten>;
   417→}
   418→
   419→export function enrichTrackingEintraege(
   420→  trackingEintraege: TrackingEintraege[],
   421→  maps: TrackingEintraegeMaps
   422→): EnrichedTrackingEintraege[] {
   423→  return trackingEintraege.map(r => ({
   424→    ...r,
   425→    gewohnheitName: resolveDisplay(r.fields.gewohnheit, maps.gewohnheitenMap, 'gewohnheit_name'),
   426→  }));
   427→}
   428→
   429→```
