   431→## src/lib/formatters.ts
   432→```
   433→import { format, parseISO } from 'date-fns';
   434→import { de } from 'date-fns/locale';
   435→
   436→export function formatDate(s: string | undefined) {
   437→  if (!s) return '—';
   438→  try { return format(parseISO(s), 'dd.MM.yyyy', { locale: de }); } catch { return s; }
   439→}
   440→
   441→export function formatCurrency(v: number | undefined) {
   442→  if (v == null) return '—';
   443→  return new Intl.NumberFormat('de-DE', { style: 'currency', currency: 'EUR' }).format(v);
   444→}
   445→
   446→/** Extract display label from lookup/select or lookup/radio API response */
   447→export function displayLookup(val: unknown): string {
   448→  if (val == null) return '—';
   449→  if (typeof val === 'object' && val !== null && 'label' in val) return String((val as Record<string, unknown>).label);
   450→  return String(val);
   451→}
   452→
   453→/** Extract labels from multiplelookup/checkbox API response */
   454→export function displayMultiLookup(val: unknown): string {
   455→  if (val == null) return '—';
   456→  if (Array.isArray(val)) return val.map(v => typeof v === 'object' && v !== null && 'label' in v ? String((v as Record<string, unknown>).label) : String(v)).join(', ') || '—';
   457→  return displayLookup(val);
   458→}
   459→
   460→/** Extract key from lookup value (handles both {key,label} objects and plain strings) */
   461→export function lookupKey(val: unknown): string | undefined {
   462→  if (val == null) return undefined;
   463→  if (typeof val === 'object' && val !== null && 'key' in val) return String((val as Record<string, unknown>).key);
   464→  if (typeof val === 'string') return val;
   465→  return undefined;
   466→}
   467→
   468→/** Extract keys from multiplelookup value */
   469→export function lookupKeys(val: unknown): string[] {
   470→  if (!Array.isArray(val)) return [];
   471→  return val.map(v => typeof v === 'object' && v !== null && 'key' in v ? String((v as Record<string, unknown>).key) : String(v));
   472→}
