   477→const AI_ENDPOINT = "https://my.living-apps.de/litellm/v1/chat/completions";
   478→const AI_MODEL = "default";
   479→
   480→export type ChatMessage = {
   481→  role: "system" | "user" | "assistant";
   482→  content: string | Array<{ type: string; [key: string]: unknown }>;
   483→};
   484→
   485→type CompletionOptions = {
   486→  temperature?: number;
   487→  max_tokens?: number;
   488→  top_p?: number;
   489→  stop?: string[];
   490→  response_format?: { type: string };
   491→};
   492→
   493→export async function chatCompletion(
   494→  messages: ChatMessage[],
   495→  options: CompletionOptions = {}
   496→): Promise<string> {
   497→  const res = await fetch(AI_ENDPOINT, {
   498→    method: "POST",
   499→    headers: { "Content-Type": "application/json", Accept: "application/json" },
   500→    body: JSON.stringify({ model: AI_MODEL, messages, ...options }),
   501→  });
   502→  if (!res.ok) {
   503→    const body = await res.text();
   504→    throw new Error(`AI API ${res.status}: ${body}`);
   505→  }
   506→  const data = await res.json();
   507→  return data.choices[0].message.content;
   508→}
   509→
   510→export async function safeJsonCompletion<T = unknown>(
   511→  messages: ChatMessage[],
   512→  options: CompletionOptions = {}
   513→): Promise<T> {
   514→  const raw = await chatCompletion(messages, options);
   515→  const match = raw.match(/\{[\s\S]*\}|\[[\s\S]*\]/);
   516→  if (!match) throw new Error(`Expected JSON but got: ${raw.slice(0, 200)}`);
   517→  return JSON.parse(match[0]);
   518→}
   519→
   520→export async function withRetry<T>(fn: () => Promise<T>, maxAttempts = 3): Promise<T> {
   521→  for (let attempt = 1; attempt <= maxAttempts; attempt++) {
   522→    try {
   523→      return await fn();
   524→    } catch (err) {
   525→      if (attempt === maxAttempts) throw err;
   526→      await new Promise((r) => setTimeout(r, 1000 * attempt));
   527→    }
   528→  }
   529→  throw new Error("unreachable");
   530→}
   531→
   532→// --- File encoding helpers ---
   533→
   534→export function fileToDataUri(file: File): Promise<string> {
   535→  return new Promise((resolve, reject) => {
   536→    const reader = new FileReader();
   537→    reader.onload = () => resolve(reader.result as string);
   538→    reader.onerror = () => reject(new Error("Failed to read file"));
   539→    reader.readAsDataURL(file);
   540→  });
   541→}
   542→
   543→export async function urlToDataUri(url: string): Promise<string> {
   544→  const res = await fetch(url);
   545→  const blob = await res.blob();
   546→  return new Promise((resolve, reject) => {
   547→    const reader = new FileReader();
   548→    reader.onload = () => resolve(reader.result as string);
   549→    reader.onerror = () => reject(new Error("Failed to encode file"));
   550→    reader.readAsDataURL(blob);
   551→  });
   552→}
   553→
   554→/** Convert a data URI back to a Blob (for file uploads) */
   555→export function dataUriToBlob(dataUri: string): Blob {
   556→  const [header, base64] = dataUri.split(',');
   557→  const mime = header.match(/:(.*?);/)?.[1] ?? 'application/octet-stream';
   558→  const bytes = atob(base64);
   559→  const arr = new Uint8Array(bytes.length);
   560→  for (let i = 0; i < bytes.length; i++) arr[i] = bytes.charCodeAt(i);
   561→  return new Blob([arr], { type: mime });
   562→}
   563→
   564→// --- High-level AI features ---
   565→
   566→export async function classify(
   567→  text: string,
   568→  categories: string[]
   569→): Promise<{ category: string; confidence: number }> {
   570→  return safeJsonCompletion([
   571→    {
   572→      role: "system",
   573→      content: [
   574→        "You are a classifier. Respond ONLY with valid JSON, nothing else.",
   575→        'Output format: {"category": "<one of the allowed categories>", "confidence": <0-1>}',
   576→        `Allowed categories: ${JSON.stringify(categories)}`,
   577→      ].join("\n"),
   578→    },
   579→    { role: "user", content: text },
   580→  ], { temperature: 0 });
   581→}
   582→
   583→export async function extract<T = Record<string, unknown>>(
   584→  text: string,
   585→  schemaDescription: string
   586→): Promise<T> {
   587→  return safeJsonCompletion([
   588→    {
   589→      role: "system",
   590→      content: [
   591→        "You are a data extraction engine. Respond ONLY with valid JSON matching the requested schema.",
   592→        "If a field cannot be determined from the input, use null.",
   593→        `Schema:\n${schemaDescription}`,
   594→      ].join("\n"),
   595→    },
   596→    { role: "user", content: text },
   597→  ], { temperature: 0 });
   598→}
   599→
   600→export async function summarize(
   601→  text: string,
   602→  options: { maxSentences?: number; language?: string } = {}
   603→): Promise<string> {
   604→  const { maxSentences = 3, language } = options;
   605→  const instructions = [
   606→    `Summarize the following text in at most ${maxSentences} sentences.`,
   607→    "Be concise and preserve key facts.",
   608→  ];
   609→  if (language) instructions.push(`Write the summary in ${language}.`);
   610→  return chatCompletion([
   611→    { role: "system", content: instructions.join(" ") },
   612→    { role: "user", content: text },
   613→  ]);
   614→}
   615→
   616→export async function translate(
   617→  text: string,
   618→  targetLanguage: string,
   619→  sourceLanguage?: string
   620→): Promise<string> {
   621→  const from = sourceLanguage ? ` from ${sourceLanguage}` : "";
   622→  return chatCompletion([
   623→    {
   624→      role: "system",
   625→      content: `Translate the following text${from} to ${targetLanguage}. Output ONLY the translation, nothing else.`,
   626→    },
   627→    { role: "user", content: text },
   628→  ]);
   629→}
   630→
   631→export async function analyzeImage(imageDataUri: string, prompt: string): Promise<string> {
   632→  return chatCompletion([
   633→    {
   634→      role: "user",
   635→      content: [
   636→        { type: "text", text: prompt },
   637→        { type: "image_url", image_url: { url: imageDataUri } },
   638→      ],
   639→    },
   640→  ]);
   641→}
   642→
   643→export async function analyzeDocument(fileDataUri: string, prompt: string): Promise<string> {
