   664→    {
   665→      role: "system",
   666→      content: [
   667→        "Extract structured data from the provided " + (isImage ? "image" : "document") + ".",
   668→        "Respond ONLY with valid JSON matching the schema.",
   669→        "Use null for any field that cannot be determined.",
   670→        `Schema:\n${schemaDescription}`,
