import { useDashboardData } from '@/hooks/useDashboardData';
import { enrichTaeglicherCheckIn, enrichTrackingEintraege } from '@/lib/enrich';
import type { EnrichedTaeglicherCheckIn, EnrichedTrackingEintraege } from '@/types/enriched';
import type { TaeglicherCheckIn, TrackingEintraege, Gewohnheiten } from '@/types/app';
import { APP_IDS } from '@/types/app';
import { LivingAppsService, extractRecordId, createRecordUrl } from '@/services/livingAppsService';
import { formatDate, formatCurrency } from '@/lib/formatters';
import { Skeleton } from '@/components/ui/skeleton';
import { AlertCircle } from 'lucide-react';
import { Button } from '@/components/ui/button';

export default function DashboardOverview() {
  const {
    taeglicherCheckIn, trackingEintraege, gewohnheiten,
    gewohnheitenMap,
    loading, error, fetchAll,
  } = useDashboardData();

  const enrichedTaeglicherCheckIn = enrichTaeglicherCheckIn(taeglicherCheckIn, { gewohnheitenMap });
  const enrichedTrackingEintraege = enrichTrackingEintraege(trackingEintraege, { gewohnheitenMap });

  if (loading) return <DashboardSkeleton />;
  if (error) return <DashboardError error={error} onRetry={fetchAll} />;

  return (
    <div className="space-y-8">
    </div>
  );
}

function DashboardSkeleton() {
  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <Skeleton className="h-8 w-48" />
        <Skeleton className="h-9 w-36" />
      </div>
      <div className="grid grid-cols-2 lg:grid-cols-4 gap-3">
        {[...Array(4)].map((_, i) => <Skeleton key={i} className="h-24 rounded-2xl" />)}
      </div>
      <Skeleton className="h-64 rounded-2xl" />
    </div>
  );
}

function DashboardError({ error, onRetry }: { error: Error; onRetry: () => void }) {
  return (
    <div className="flex flex-col items-center justify-center py-24 gap-4">
      <div className="w-12 h-12 rounded-2xl bg-destructive/10 flex items-center justify-center">
        <AlertCircle size={22} className="text-destructive" />
      </div>
      <div className="text-center">
        <h3 className="font-semibold text-foreground mb-1">Fehler beim Laden</h3>
        <p className="text-sm text-muted-foreground max-w-xs">{error.message}</p>
      </div>
      <Button variant="outline" size="sm" onClick={onRetry}>Erneut versuchen</Button>
    </div>
  );
}

