   355→                        className="p-1.5 rounded-lg hover:bg-destructive/10 transition-colors text-muted-foreground hover:text-destructive"
   356→                      >
   357→                        <Trash2 size={14} />
   358→                      </button>
   359→                    </div>
   360→                  </div>
   361→                );
   362→              })}
   363→            </div>
   364→          )}
   365→        </div>
   366→
   367→        {/* Weekly Habit Grid */}
   368→        <div className="xl:col-span-2 bg-card border border-border rounded-2xl overflow-hidden">
   369→          <div className="px-5 py-4 border-b border-border">
   370→            <h2 className="font-semibold text-foreground">7-Tage-Übersicht</h2>
   371→            <p className="text-xs text-muted-foreground mt-0.5">Letzte 7 Tage</p>
   372→          </div>
   373→
   374→          {activeHabits.length === 0 ? (
   375→            <div className="flex items-center justify-center py-12 text-sm text-muted-foreground">
   376→              Keine aktiven Gewohnheiten
   377→            </div>
   378→          ) : (
   379→            <div className="p-4 overflow-x-auto">
   380→              <table className="w-full text-xs">
   381→                <thead>
   382→                  <tr>
   383→                    <th className="text-left text-muted-foreground font-medium pb-2 pr-2 max-w-[100px]">Gewohnheit</th>
   384→                    {last7Days.map(d => (
   385→                      <th key={d.toISOString()} className={`text-center pb-2 px-1 font-medium w-8 ${isSameDay(d, selectedDate) ? 'text-primary' : 'text-muted-foreground'}`}>
   386→                        {format(d, 'EE', { locale: de }).slice(0, 2)}
   387→                      </th>
   388→                    ))}
   389→                  </tr>
   390→                </thead>
   391→                <tbody>
   392→                  {activeHabits.map(habit => (
   393→                    <tr key={habit.record_id} className="border-t border-border/40">
   394→                      <td className="py-2 pr-2 text-foreground font-medium truncate max-w-[90px]" title={habit.fields.gewohnheit_name}>
   395→                        {(habit.fields.gewohnheit_name ?? '?').slice(0, 12)}{(habit.fields.gewohnheit_name?.length ?? 0) > 12 ? '…' : ''}
   396→                      </td>
   397→                      {last7Days.map(d => {
   398→                        const ds = format(d, 'yyyy-MM-dd');
   399→                        const entries = trackingByDateAndHabit.get(ds)?.get(habit.record_id) ?? [];
   400→                        const st = entries.length > 0 ? lookupKey(entries[0].fields.status) : null;
   401→                        return (
   402→                          <td key={d.toISOString()} className="text-center py-2 px-1">
   403→                            {st === 'erledigt' ? (
   404→                              <span className="inline-flex items-center justify-center w-6 h-6 rounded-full bg-emerald-500 text-white">
   405→                                <CheckCircle2 size={12} />
   406→                              </span>
   407→                            ) : st === 'uebersprungen' ? (
   408→                              <span className="inline-flex items-center justify-center w-6 h-6 rounded-full bg-yellow-100">
   409→                                <SkipForward size={10} className="text-yellow-600" />
   410→                              </span>
   411→                            ) : st === 'teilweise' ? (
   412→                              <span className="inline-flex items-center justify-center w-6 h-6 rounded-full bg-blue-100">
   413→                                <Minus size={10} className="text-blue-600" />
   414→                              </span>
   415→                            ) : (
   416→                              <span className="inline-flex items-center justify-center w-6 h-6 rounded-full bg-muted/50 text-muted-foreground/30">
   417→                                <Circle size={10} />
   418→                              </span>
   419→                            )}
   420→                          </td>
   421→                        );
   422→                      })}
   423→                    </tr>
   424→                  ))}
   425→                </tbody>
   426→              </table>
   427→            </div>
   428→          )}
   429→
   430→          {/* Legend */}
   431→          <div className="px-4 pb-4 flex gap-3 flex-wrap">
   432→            <span className="flex items-center gap-1.5 text-xs text-muted-foreground">
   433→              <span className="w-3 h-3 rounded-full bg-emerald-500 inline-block" /> Erledigt
   434→            </span>
   435→            <span className="flex items-center gap-1.5 text-xs text-muted-foreground">
   436→              <span className="w-3 h-3 rounded-full bg-yellow-200 inline-block" /> Übersprungen
   437→            </span>
   438→            <span className="flex items-center gap-1.5 text-xs text-muted-foreground">
   439→              <span className="w-3 h-3 rounded-full bg-blue-200 inline-block" /> Teilweise
   440→            </span>
   441→          </div>
   442→        </div>
   443→      </div>
   444→
   445→      {/* Recent Tracking Entries */}
   446→      <div className="bg-card border border-border rounded-2xl overflow-hidden">
   447→        <div className="px-5 py-4 border-b border-border flex items-center justify-between">
   448→          <div>
   449→            <h2 className="font-semibold text-foreground">Letzte Einträge</h2>
   450→            <p className="text-xs text-muted-foreground mt-0.5">{trackingEintraege.length} Einträge gesamt</p>
   451→          </div>
   452→          <Button
   453→            size="sm"
   454→            variant="outline"
   455→            onClick={() => { setEditTracking(null); setPrefilledGewohnheit(null); setTrackDialog(true); }}
   456→            className="gap-2"
   457→          >
   458→            <Plus size={14} /> Eintrag hinzufügen
   459→          </Button>
   460→        </div>
   461→
   462→        {trackingEintraege.length === 0 ? (
   463→          <div className="flex flex-col items-center justify-center py-12 text-center px-6">
   464→            <p className="text-sm text-muted-foreground">Noch keine Tracking-Einträge.</p>
   465→          </div>
   466→        ) : (
   467→          <div className="divide-y divide-border">
   468→            {enrichTrackingEintraege(
   469→              [...trackingEintraege]
   470→                .sort((a, b) => (b.fields.datum_uhrzeit ?? '').localeCompare(a.fields.datum_uhrzeit ?? ''))
   471→                .slice(0, 8),
   472→              { gewohnheitenMap }
   473→            ).map(entry => {
   474→              const st = lookupKey(entry.fields.status);
   475→              return (
   476→                <div key={entry.record_id} className="px-5 py-3 flex items-center gap-4 hover:bg-accent/20 transition-colors">
   477→                  <div className="shrink-0">
   478→                    {st ? STATUS_ICONS[st as keyof typeof STATUS_ICONS] : <Circle size={18} className="text-muted-foreground/30" />}
   479→                  </div>
   480→                  <div className="flex-1 min-w-0">
   481→                    <div className="flex items-center gap-2 flex-wrap">
   482→                      <span className="font-medium text-sm text-foreground truncate">
   483→                        {entry.gewohnheitName || '—'}
   484→                      </span>
   485→                      {st && (
   486→                        <Badge variant="outline" className={`text-xs py-0 ${
   487→                          st === 'erledigt' ? 'border-emerald-200 text-emerald-700 bg-emerald-50' :
   488→                          st === 'uebersprungen' ? 'border-yellow-200 text-yellow-700 bg-yellow-50' :
   489→                          'border-blue-200 text-blue-700 bg-blue-50'
   490→                        }`}>
   491→                          {st === 'erledigt' ? 'Erledigt' : st === 'uebersprungen' ? 'Übersprungen' : 'Teilweise'}
   492→                        </Badge>
   493→                      )}
   494→                      {entry.fields.bewertung != null && (
   495→                        <span className="flex items-center gap-0.5 text-xs text-yellow-600">
   496→                          <Star size={11} /> {entry.fields.bewertung}
   497→                        </span>
   498→                      )}
   499→                    </div>
   500→                    <div className="flex items-center gap-3 mt-0.5">
   501→                      <span className="text-xs text-muted-foreground">
   502→                        {entry.fields.datum_uhrzeit
   503→                          ? format(parseISO(entry.fields.datum_uhrzeit), 'dd.MM.yyyy, HH:mm', { locale: de })
   504→                          : '—'}
   505→                      </span>
   506→                      {entry.fields.notizen && (
   507→                        <span className="text-xs text-muted-foreground truncate max-w-[200px]">{entry.fields.notizen}</span>
   508→                      )}
   509→                    </div>
   510→                  </div>
   511→                  <div className="flex items-center gap-1 shrink-0">
   512→                    <button
   513→                      onClick={() => { setEditTracking(entry); setPrefilledGewohnheit(null); setTrackDialog(true); }}
   514→                      className="p-1.5 rounded-lg hover:bg-accent transition-colors text-muted-foreground"
   515→                    >
   516→                      <Edit2 size={14} />
   517→                    </button>
   518→                    <button
   519→                      onClick={() => setDeleteTracking(entry)}
   520→                      className="p-1.5 rounded-lg hover:bg-destructive/10 transition-colors text-muted-foreground hover:text-destructive"
   521→                    >
   522→                      <Trash2 size={14} />
   523→                    </button>
   524→                  </div>
   525→                </div>
   526→              );
   527→            })}
   528→          </div>
   529→        )}
   530→      </div>
   531→
   532→      {/* Dialogs */}
   533→      <GewohnheitenDialog
   534→        open={gwDialog}
   535→        onClose={() => { setGwDialog(false); setEditGewohnheit(null); }}
   536→        onSubmit={async (fields) => {
   537→          if (editGewohnheit) {
   538→            await LivingAppsService.updateGewohnheitenEntry(editGewohnheit.record_id, fields);
   539→          } else {
   540→            await LivingAppsService.createGewohnheitenEntry(fields);
   541→          }
   542→          fetchAll();
   543→        }}
   544→        defaultValues={editGewohnheit?.fields}
   545→        enablePhotoScan={AI_PHOTO_SCAN['Gewohnheiten']}
   546→      />
   547→
   548→      <TrackingEintraegeDialog
   549→        open={trackDialog}
   550→        onClose={() => { setTrackDialog(false); setEditTracking(null); setPrefilledGewohnheit(null); }}
   551→        onSubmit={async (fields) => {
   552→          if (editTracking) {
   553→            await LivingAppsService.updateTrackingEintraegeEntry(editTracking.record_id, fields);
   554→          } else {
   555→            await LivingAppsService.createTrackingEintraegeEntry(fields);
   556→          }
   557→          fetchAll();
   558→        }}
   559→        defaultValues={
   560→          editTracking
   561→            ? editTracking.fields
   562→            : prefilledGewohnheit
   563→            ? { gewohnheit: createRecordUrl(APP_IDS.GEWOHNHEITEN, prefilledGewohnheit) }
   564→            : undefined
   565→        }
   566→        gewohnheitenList={gewohnheiten}
   567→        enablePhotoScan={AI_PHOTO_SCAN['TrackingEintraege']}
   568→      />
   569→
   570→      <ConfirmDialog
   571→        open={!!deleteGewohnheit}
   572→        title="Gewohnheit löschen"
   573→        description={`„${deleteGewohnheit?.fields.gewohnheit_name}" wirklich löschen? Alle Tracking-Einträge bleiben erhalten.`}
   574→        onConfirm={async () => {
   575→          if (!deleteGewohnheit) return;
   576→          await LivingAppsService.deleteGewohnheitenEntry(deleteGewohnheit.record_id);
   577→          setDeleteGewohnheit(null);
   578→          fetchAll();
   579→        }}
   580→        onClose={() => setDeleteGewohnheit(null)}
   581→      />
   582→
   583→      <ConfirmDialog
   584→        open={!!deleteTracking}
   585→        title="Eintrag löschen"
   586→        description="Diesen Tracking-Eintrag wirklich löschen?"
   587→        onConfirm={async () => {
   588→          if (!deleteTracking) return;
   589→          await LivingAppsService.deleteTrackingEintraegeEntry(deleteTracking.record_id);
   590→          setDeleteTracking(null);
   591→          fetchAll();
   592→        }}
   593→        onClose={() => setDeleteTracking(null)}
   594→      />
   595→    </div>
   596→  );
   597→}
   598→
   599→function DashboardSkeleton() {
   600→  return (
   601→    <div className="space-y-6">
   602→      <div className="flex items-center justify-between">
   603→        <Skeleton className="h-8 w-48" />
   604→        <Skeleton className="h-9 w-36" />
   605→      </div>
   606→      <div className="grid grid-cols-2 lg:grid-cols-4 gap-3">
   607→        {[...Array(4)].map((_, i) => <Skeleton key={i} className="h-24 rounded-2xl" />)}
   608→      </div>
   609→      <Skeleton className="h-64 rounded-2xl" />
   610→    </div>
   611→  );
   612→}
   613→
   614→function DashboardError({ error, onRetry }: { error: Error; onRetry: () => void }) {
   615→  return (
   616→    <div className="flex flex-col items-center justify-center py-24 gap-4">
   617→      <div className="w-12 h-12 rounded-2xl bg-destructive/10 flex items-center justify-center">
   618→        <AlertCircle size={22} className="text-destructive" />
   619→      </div>
   620→      <div className="text-center">
   621→        <h3 className="font-semibold text-foreground mb-1">Fehler beim Laden</h3>
   622→        <p className="text-sm text-muted-foreground max-w-xs">{error.message}</p>
